import React, {useState, useEffect} from "react";
import './Home.css';
import axios from "axios";

export default function Home(){

    var url = "https://jooble.org/api/jobinfo";
    var key = "sgsg09AnrGoD87jbverhjevrAFIfger27g83";
    var params = "{ keywords: 'it', location: 'Bern'}"

    //create xmlHttpRequest object
    var http = new XMLHttpRequest();
    //open connection. true - asynchronous, false - synchronous
    http.open("POST", url + key, true);

    //Send the proper header information
    http.setRequestHeader("Content-type", "application/json");
      
    //Callback when the state changes
    http.onreadystatechange = function() {
      if(http.readyState == 4 && http.status == 200) {
        alert(http.responseText);
      }
    }
    //Send request to the server
    http.send(params);

    const [count, setCount] = useState(0);
    const [data, setData] = useState('');
  
    const prev = () => {
      const nextCount = count - 1;
      count === 0 ? setCount(gitUsername.length - 1) : setCount(nextCount);
    }; //buat tombol sebelumnya
  
    const next = () => {
      const nextCount = count + 1;
      count === gitUsername.length - 1 ? setCount(0) : setCount(nextCount);
    }; //tombol setelahnya
  
    useEffect(() => {
      const fetchData = async () => {
        await axios
          .get(`https://findwork.dev/api/jobs/?page=2${gitUsername[count]}`)
          .then((res) => setData(res.data))
          .catch((error) => alert('Gagal!', error));
      };
      fetchData();
    }, [count]);
  
    return (
      <div className="container">
        <h2>Github Kelompok 02</h2>
        <div className="name-person">{data.name}</div>
        <div className="img">
          <img src={data.avatar_url} alt={data.login} />
        </div>
        <div className="repo">{data.public_repos} Repository</div>
        <div className="username">{data.login}</div>
        <div className="keterangan">
          <div className="followers">{data.followers} pengikut</div>
          <div className="following">{data.following} mengikuti</div>
        </div>
        <table>
          <tr>
            <td>
              <div className="backward" onClick={prev}>
                <p><b>PREVIEW</b></p>
              </div>
            </td>
            <td>
              <div className="forward" onClick={next}>
                <p><b>NEXT</b></p>
              </div>
            </td>
          </tr>
        </table>
      </div>
    );
}
