import React, {useState, useEffect} from "react";
import {getPhotosList, searchJob, searchPhotos} from "./Api";
import '../Component/Home.css';
import axios from "axios";

export default function Home(){
    const [photos, setPhotos] = useState([]);
    
    const key = '31657769-cc724a3f600ddb772dcd00e99';
    let keyword = 'flower';
    const getPhotos = async() => {

      try {
        let response = await axios({ 
          method: 'GET', 
                // 'auth-token': process.env.REACT_APP_API_KEY
          url: `https://pixabay.com/api/?key=${key}&keyword=${keyword}`

          });
  
          setPhotos(response.data.hits)
      }catch (e) {
      console.log(e.message)}
      
      useEffect(() => {
            // 'auth-token': process.env.REACT_APP_API_KEY
        getPhotos();
            });
        }

    const PhotosList = () => {  
        photos.map((gambar, index) =>{
          return(
            <div className="card-job" key={photos.id}>
            <h4>Dibutuhkan</h4>

            <div className="name-job"><b>{photos.user}</b></div>
            <div className="img">
              <img src={photos.previewURL}/>
            </div>
            <div className="company">{photos.tags}</div>
            <div className="tingkat">Likes :{photos.likes}</div>
            <div className="region">Views :{photos.views}</div> 
            <div className="region">{photos.type}</div> 
          </div>

        )
          });
    }

    return (
      <div className="App">
        <h2>JOB Information System</h2>
        {/* <input placeholder="Search Job..." className="search-box" onChange={({target}) => search(target.value)} /> */}
        <div className="card-job">
          <PhotosList />
        </div>
      </div>
    );
    }
  


