import React, {useState, useEffect} from "react";
import {getPhotosList, searchJob} from "./Api";
import '../Component/Home.css';
import axios from "axios";

export default function Home(){
    const [photos, setPhotos] = useState([])

    useEffect(() => {
      var API_KEY = '31657769-cc724a3f600ddb772dcd00e99';
      var URL = "https://pixabay.com/api/?key="+API_KEY+"&q="+encodeURIComponent('red roses');
      $.getJSON(URL, function(data){
      if (parseInt(data.totalHits) > 0)
          $.each(data.hits, function(i, hit){ console.log(hit.pageURL); });
      else
          console.log('No hits');
      });
    });

    const search = (q) => {
      const photos = searchJob(q)
      console.log({search})
    }

    const PhotosList = () => {
      return photos.map((job, i) => {
        return(
            <div className="card-job"  key={i}>
            <h4>Dibutuhkan</h4>
            <div className="name-job"><b>{job.user}</b></div>
            <div className="img">
              <img src={`${process.env.REACT_APP_IMG}/${job.previewURL}`}/>
            </div>
            <div className="company">{job.tags}</div>
            <div className="tingkat">Likes :{job.likes}</div>
            <div className="region">Views :{job.views}</div> 
            <div className="region">{job.type}</div> 
          </div>
        )
      });
    }

}
