import React, {useState, useEffect} from "react";
import {getJobList, searchJob} from "./Api";
import '../Component/Home.css';
import axios from "axios";
export default function Home(){

  const Home = () => {
    const [dribbleJob, setdribbleJob] = useState([])

    useEffect(() => {
      getJobList().then((result) => {
        setdribbleJob(result)
      })
    }, [])

    const dribbleJobList = () => {
      return dribbleJob.map((job, i) => {
        return(
            <div className="card-job"  key={i}>
            <h4>Dibutuhkan</h4>
            <div className="name-job"><b>{job.role}</b></div>
            <div className="img">
              <img src={`${process.env.REACT_APP_IMG}/${job.logo}`}/>
            </div>
            <div className="company">{job.company_name}</div>
            <div className="tingkat">{job.employment_type}</div>
            <div className="region">{job.location}</div> 
            <div className="region">{job.date_posted}</div> 
          </div>
        )
      })
    }
    
    const search = (q) => {
      console.log({ q })
    }
  }
  
    return (
      <div className="App">
        <h2>JOB Information System</h2>
        <input placeholder="Search Job..." className="search-box" ocChange={({target}) => searchJob(target.value)} />
        <div className="card-job">
          <dribbleJobList />
        </div>
      </div>
    );
}
