import React, {useState, useEffect} from "react";
import {getPhotosList, searchJob, searchPhotos} from "./Api";
import '../Component/Home.css';
import axios from "axios";

export default function Home(){
    const [photos, setPhotos] = useState('');

    useEffect(() => {
      const fetchData = async() =>{
        await axios
          .get(`https://pixabay.com/api/31657769-cc724a3f600ddb772dcd00e99`)
          .then((res) => setPhotos(res.data))
          .catch((error) => alert('Gagal!', error));
      };
      fetchData();
    }, []);
  
    const PhotosList = () => {
        return (
            <div className="card-job">
            <h4>Dibutuhkan</h4>
            <div className="name-job"><b>{photos.user}</b></div>
            <div className="img">
              <img src={`${process.env.REACT_APP_IMG}/${photos.previewURL}`}/>
            </div>
            <div className="company">{photos.tags}</div>
            <div className="tingkat">Likes :{photos.likes}</div>
            <div className="region">Views :{photos.views}</div> 
            <div className="region">{photos.type}</div> 
          </div>
        )
    }

    return (
      <div className="App">
        <h2>JOB Information System</h2>
        <input placeholder="Search Job..." className="search-box" onChange={({target}) => search(target.value)} />
        <div className="card-job">
          <PhotosList />
        </div>
      </div>
    );
    }
