import React, {useState, useEffect} from "react";
import {getJobList, searchJob} from "./Api";
import './Home.css';
import axios from "axios";
import { getSearchParamsForLocation } from "react-router-dom/dist/dom";

export default function Home(){

  const Home = () => {
    const search = (q) => {
      console.log({ q })
    }
  }
    const [count, setCount] = useState(0);
    const [data, setData] = useState('');
  
    const prev = () => {
      const nextCount = count - 1;
      count === 0 ? setCount(gitUsername.length - 1) : setCount(nextCount);
    }; //buat tombol sebelumnya
  
    const next = () => {
      const nextCount = count + 1;
      count === gitUsername.length - 1 ? setCount(0) : setCount(nextCount);
    }; //tombol setelahnya
  
    useEffect(() => {
      getJobList()
      const fetchData = async () => {
        await axios
          .get(`https://findwork.dev/api/jobs/?page=2${gitUsername[count]}`)
          .then((res) => setData(res.data))
          .catch((error) => alert('Gagal!', error));
      };
      fetchData();
    }, [count]);
  
    return (
      <div className="container">
        <h2>JOB Information System</h2>
        <input placeholder="Search Job..." className="search-box" ocChange={({target}) => search(target.value)} />
        
        //kotak pekerjaan
        <div className="card-job">
          <h4>Dibutuhkan</h4>
          <div className="name-job">{data.name}</div>
          <div className="img">
            <img src={data.avatar_url} alt={data.login} />
          </div>
          <div className="company">{data.public_repos} Repository</div>
          <div>
          <span className="tingkat">{data.login}</span>
          <span className="keterangan"></span>
          </div>
          <div className="region">{data.followers} pengikut</div> 
        </div>

      </div>
    );
}
