import React, {useState, useEffect} from "react";
import {getJobList, searchJob} from "./Api";
import './Home.css';
import axios from "axios";
import { getSearchParamsForLocation } from "react-router-dom/dist/dom";

export default function Home(){

  const Home = () => {
    const [dribbleJob, setdribbleMovies] = useState([])

    useEffect(() => {
      getJobList().then((result) => {
        setdribbleJob(result)
      })
    }, [])

    const dribbleJobList = () => {
      return dribbleJob.map((job, i) => {
        return(
          <div key={i}>
            <div>{job.role}</div>
            <div>{job.logo}</div>
            <div>{job.company_name}</div>
            <div>{job.employment_type}</div>
            <div>{job.location}</div>
            <div>{job.date_posted}</div>

            <div className="card-job">
            <h4>Dibutuhkan</h4>
            <div className="name-job"><b>{job.role}</b></div>
            <div className="img">
              <img src={job.logo} alt={data.login} />
            </div>
            <div className="company">{job.company_name}</div>
            <div className="tingkat">{job.employment_type}</div>
            <div className="region">{job.location}</div> 
            <div className="region">{job.date_posted}</div> 
          </div>
          </div>
        )
      })
    }
    
    const search = (q) => {
      console.log({ q })
    }
  }
  
    return (
      <div className="container">
        <h2>JOB Information System</h2>
        <input placeholder="Search Job..." className="search-box" ocChange={({target}) => search(target.value)} />
        
        <div className="card-job">
          <h4>Dibutuhkan</h4>
          <div className="name-job"><b>{data.name}</b></div>
          <div className="img">
            <img src={data.avatar_url} alt={data.login} />
          </div>
          <div className="company">{data.public_repos} Repository</div>
          <div>
          <span className="tingkat">{data.login}</span>
          <span className="keterangan"></span>
          </div>
          <div className="region">{data.followers} pengikut</div> 
        </div>

      </div>
    );
}
