import React, {useState, useEffect} from "react";
import {getJobList, searchJob} from "./Api";
import '../Component/Home.css';
import axios from "axios";

export default function Home(){
    const [photos, setPhotos] = useState([]);

    useEffect(() => {
      const fetchData = async(photos) => {
        try{
          const response = await axios.get(
            'https://pixabay.com/api/?key=${REACT_APP_APIKEY}&q=${}',{
              params: {q: photos},
              headers: {
                'API_KEY': '31657769-cc724a3f600ddb772dcd00e99'
              }
            }
          );
          setPhotos(response.data)
        }catch(err){
          console.log(response);
        }
      }
    }, [])

    const PhotosList = (fetchData) => {
      return photos.map((job, i) => {
        return(
            <div className="card-job"  key={i}>
            <h4>Dibutuhkan</h4>
            <div className="name-job"><b>{job.user}</b></div>
            <div className="img">
              <img src={`${process.env.REACT_APP_IMG}/${job.previewURL}`}/>
            </div>
            <div className="company">{job.tags}</div>
            <div className="tingkat">Likes :{job.likes}</div>
            <div className="region">Views :{job.views}</div> 
            <div className="region">{job.type}</div> 
          </div>
        )
      })
    }
    
    const search = async(q) => {
      const photos = await searchJob(q)
      console.log({photos: photos})
    }
  
    return (
      <div className="App">
        <h2>JOB Information System</h2>
        <input placeholder="Search Job..." className="search-box" onChange={({target}) => search(target.value)} />
        <div className="card-job">
          <PhotosList />
        </div>
      </div>
    );
}
